export async function createJobVerified(job: Job) {
    await collections.verifiedJobs.insertOne(job)
}
export async function findJob(jobId: string): Promise<Job> {
    const job = await collections.verifiedJobs.findOne({id: jobId})
    if(job) return job
    else return zeroJob
}
export async function deleteJob(jobId: string) {
    collections.verifiedJobs.deleteOne({id: jobId})
}
export async function findJobByTag(userTag: string): Promise<Job> {
    const job = await collections.verifiedJobs.findOne({userTag: userTag})
    if(job) return job
    else return zeroJob
}
export async function addProposal(userId: string, jobId: string) {
    let job = await findJob(jobId)
    job.proposals.push(userId)
    collections.verifiedJobs.updateOne({id: jobId}, {$set: job})
}
export async function bumpPost(jobId: string) {
    let job = await findJob(jobId)
    job.bumpDate = Date.now()
    collections.verifiedJobs.updateOne({id: jobId}, {$set: job})
}
export async function findJobs(userId: string) {
    let jobs = await collections.verifiedJobs.find({userId: userId, jobType: 'Hire'}).toArray()
    const unverfiedJobs = await collections.unverifiedJobs.find({userId: userId, jobType: 'Hire'}).toArray()
    unverfiedJobs.forEach((job)=>{jobs.push(job)})
    return jobs
}
export async function rejectJob(jobId: string) {
    let job = collections.unverifiedJobs.findOne({id: jobId})
    collections.unverifiedJobs.deleteOne({id: jobId})
    return job || zeroJob
}
export async function approveJob(jobId: string) {
    let job = await collections.unverifiedJobs.findOne({id: jobId})
    if(job) {
        rejectJob(job.id)
        collections.verifiedJobs.insertOne(job)
    }
    return job || zeroJob
}
export async function updateMessage(jobId: string, messageId: string, messageUrl: string) {
    let job = await collections.verifiedJobs.findOne({id: jobId})
    if(job) {
        job.message.id = messageId
        job.message.url = messageUrl
        collections.verifiedJobs.updateOne({id: jobId}, {$set: job})
    }
    return job
}
export async function existsUser(userId: string): Promise<boolean> {
    let user = await collections.users.findOne({userId: userId})
    if(user) return true
        else return false
}
export async function addUser(user: User) {
    collections.users.insertOne(user)
}
export async function addSkill(userId: string, userTag: string, skill: string) {
    let user = await collections.users.findOne({userId: userId})
    if(user) {
        user.skills.push(skill)
        collections.users.updateOne({userId: userId}, {$set: user})
    } else {
        addUser({
            userId: userId,
            userTag: userTag,
            skills: [skill],
            reviews: []
        })
    }
}
export async function removeSkill(userId: string, userTag: string, skill: string) {
    let user = await collections.users.findOne({userId: userId})
    if(user) {
        let tempSkills: string[] = []
        user.skills.forEach(sk => {
            if(sk !== skill) tempSkills.push(sk)
        })
        user.skills = tempSkills
        collections.users.updateOne({userId: userId}, {$set: user})
    } else {
        addUser({
            userId: userId,
            userTag: userTag,
            skills: [],
            reviews: []
        })
    }
}
export async function addReview(userId: string, review:Review) {
    let user = await collections.users.findOne({userId: userId})
    if(user) {
        user?.reviews.push(review)
        collections.users.updateOne({userId: userId}, {$set: user})
    }
}
export async function getUser(userId: string): Promise<User> {
    let user = await collections.users.findOne({userId: userId})
    return user || {userId: '', userTag: '', reviews: [], skills: []}
}
export async function updateReview(userId: string, reviews: Array<Review>) {
    let user = await collections.users.findOne({userId: userId})
    if(user) {
        user.reviews = reviews
        collections.users.updateOne({userId: userId}, {$set: user})
    }
}
export async function insertReview(review: ReviewDB) {
    collections.reviews.insertOne(review)
}
export async function getReview(reviewId: string): Promise<ReviewDB> {
    const review = await collections.reviews.findOne({id: reviewId})
    const zeroReview: ReviewDB = {
        id: "",
        reviewer: {
            userId: "",
            userTag: ""
        },
        freelancer: {
            userId: "",
            userTag: ""
        },
        review: "",
        stars: 0
    }
    return review || zeroReview
}
export async function deleteReview(reviewId: string) {
    collections.reviews.deleteOne({id: reviewId})
}